  {
    amount: "100 - 150",
  },
  {
    amount: "150 - 200",
  },
  {
    amount: "200 - 250",
  },
  {
    amount: "250 - 300",
  },
  {
    amount: "300 - 350",
  },
  {
    amount: "350 - 400",
