  }
  if (brand) {
    productUrl = `${productUrl}&brand=${brand}`;
  }
  if (size) {
    productUrl = `${productUrl}&size=${size}`;
  }
  if (price) {
    productUrl = `${productUrl}&price=${price}`;
  }
  if (color) {
    productUrl = `${productUrl}&color=${color?.name}`;
